import 'package:flutter/material.dart';
import 'package:google_fonts/google_fonts.dart';

class MockupScreenTwo extends StatelessWidget {
  final List<Map<String, String>> popular = [
    {
      "title": "Birds of Prey",
      "image": "data:image/jpeg;base64,",
      "genre": "Action, Crime",
      "rating": "7.5"
    },
    {
      "title": "Red Sparrow",
      "image": "data:image/jpeg;base64,",
      "genre": "Thriller, Spy",
      "rating": "6.5"
    },
    {
      "title": "The Batman",
      "image": "data:image/jpeg;base64,",
      "genre": "Action, Crime",
      "rating": "8.0"
    },
  ];

  final List<Map<String, String>> newPlaying = [
    {
      "title": "To all the boys i Loved before",
      "image": "https://via.placeholder.com/150x220",
      "genre": "Romance, Comedy",
      "rating": "7.0"
    },
    {
      "title": "Ford v Ferrari",
      "image": "https://via.placeholder.com/150x220",
      "genre": "Drama, Action",
      "rating": "6.8"
    },
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        backgroundColor: Colors.deepPurple,
        title: Row(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: [
            Text('Movies', style: GoogleFonts.poppins(fontSize: 20, fontWeight: FontWeight.bold)),
            ElevatedButton(
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.blue,
                padding: const EdgeInsets.symmetric(horizontal: 12, vertical: 6),
                shape: RoundedRectangleBorder(
                  borderRadius: BorderRadius.circular(20),
                ),
                minimumSize: const Size(60, 30),
                elevation: 0,
              ),
              onPressed: () {
                // Action for TMDB button
              },
              child: Text(
                'TMDB',
                style: GoogleFonts.poppins(fontSize: 14, color: Colors.white),
              ),
            ),
          ],
        ),
      ),
      body: SingleChildScrollView(
        child: Padding(
          padding: const EdgeInsets.all(16),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              // Upper image like first screen (placeholder)
              ClipRRect(
                borderRadius: BorderRadius.circular(16),
                child: Image.network(
                  "data:image/jpeg;base64,",
                  width: double.infinity,
                  height: 180,
                  fit: BoxFit.cover,
                ),
              ),
              const SizedBox(height: 20),

              Text(
                "What's Popular",
                style: GoogleFonts.poppins(fontSize: 20, fontWeight: FontWeight.bold),
              ),
              const SizedBox(height: 10),
              _buildHorizontalList(popular),

              const SizedBox(height: 30),
              Text(
                "Now Playing",
                style: GoogleFonts.poppins(fontSize: 20, fontWeight: FontWeight.bold),
              ),
              const SizedBox(height: 10),
              _buildHorizontalList(newPlaying),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildHorizontalList(List<Map<String, String>> movies) {
    return SizedBox(
      height: 230,
      child: ListView.builder(
        scrollDirection: Axis.horizontal,
        itemCount: movies.length,
        itemBuilder: (context, index) {
          final movie = movies[index];
          return Container(
            width: 140,
            margin: const EdgeInsets.only(right: 12),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Stack(
                  children: [
                    ClipRRect(
                      borderRadius: BorderRadius.circular(12),
                      child: Image.network(
                        movie["image"]!,
                        height: 180,
                        width: 140,
                        fit: BoxFit.cover,
                      ),
                    ),
                    Positioned(
                      top: 8,
                      right: 8,
                      child: Container(
                        padding: const EdgeInsets.symmetric(horizontal: 6, vertical: 4),
                        decoration: BoxDecoration(
                          color: Colors.black54,
                          borderRadius: BorderRadius.circular(12),
                        ),
                        child: Row(
                          children: [
                            const Icon(Icons.star, color: Colors.yellow, size: 14),
                            const SizedBox(width: 4),
                            Text(
                              movie["rating"] ?? "N/A",
                              style: GoogleFonts.poppins(color: Colors.white, fontWeight: FontWeight.bold, fontSize: 12),
                            ),
                          ],
                        ),
                      ),
                    ),
                  ],
                ),
                const SizedBox(height: 8),
                Text(
                  movie["title"]!,
                  style: GoogleFonts.poppins(fontWeight: FontWeight.bold),
                  maxLines: 1,
                  overflow: TextOverflow.ellipsis,
                ),
                Text(
                  movie["genre"] ?? '',
                  style: GoogleFonts.poppins(color: Colors.grey[700], fontSize: 12),
                  maxLines: 1,
                  overflow: TextOverflow.ellipsis,
                ),
              ],
            ),
          );
        },
      ),
    );
  }
}
